import React, { useState } from 'react';

var base = 0;

export function simples(e) {
    e.preventDefault();
    base = 1;
    alert(base);
}
export function composto(e) {
    e.preventDefault();
    base = 2;
    alert(base);
}

export function menu() {
    if (base === 1) {
        alert(base)
    }
    if (base === 2) {
        alert(base)
    }
}
