import { render } from 'react-dom';
import './style.css';
import { Simples, Composto, Main } from './convJuros.js';
import { CompoundInterest } from './jurosComp.js';
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import {sup, simples, composto, menu} from "./botoes.js";



export default function Home() {

var base = 0;

  function simples(e) {
    e.preventDefault();
    base=1;
  }
  function composto(e) {
    e.preventDefault();
    base=2;
  }

  function menu() {
    if(base===1){
      alert(base)
    }
    if(base===2){
      alert(base)
    }
  }

return (
  <div>
    <header>
      <div class="container" />
      <div id="logo">
        <img src="logo.png" alt="texto" height="400" width="400" />
      </div>
    </header>
    <div class="containerCentro1">
      <div class="containerCentro2">
        <div id="menu">

          <form onSubmit={simples} class="esq">
          <button class="button" type="submit">
            Interest Converter
          </button>
          </form>
          <form onSubmit={composto} class="dir">
          <button class="button" type="submit">
            Compound Interest Converter
          </button>
          </form>

        </div>
      </div>
    </div>
    <div id="principal">

      <div class="efeito">

        <div class="container">



        </div>
      </div>
    </div>
    <footer>Developed by Lucas Marinho</footer>
  </div>
)
}



render(<Home />, document.getElementById('root'));
