import React, { useState } from 'react';
import { render } from 'react-dom';
import './style.css';

export default function Home(){
  return(
    <div>
      <link rel="icon" href="icon.png" />
      <title> Juros </title>
      <body>
        <header>
          <div class="container"/>
            <div id="logo">
              <img src="logo.png" height="400" width="400"/>
            </div>
        </header>
        <div class="containerCentro1">
        <div class="containerCentro2">
            <div id="menu">
              <b class="btn btn-warning" data-toggle="collapse" href="#sim" data-target="#sim">Conversor de Juros</b>
              <a href="index2.html">Conversor de Juros Compostos</a>
            </div>  
          </div>
          </div>
          <div id="principal">

        <div class="efeito"> 

          <div class="container">

            <h1>Conversor de Juros</h1>

            <div class="collapse" id="sim"></div>
            <br/>
            <br/>
            <hr/>
            <br/>
            <br/>
            <div class="collapse" id="comp"></div>
      </div>
    </div>
  </div>
  <footer>Desenvolvido por Lucas Marinho</footer>
      </body>
    </div>


  )
}
//Convertor Simples


function convertToMensalSimples(valoranualSimples) {
  return (valoranualSimples / 12);
}

function convertToAnualSimples(valormensalSimples) {
  return (valormensalSimples * 12);
}

function ConverterInput({ label, valor, onChange }) {
  return (
    <>
      <label labelfor={label}>{label}</label>
      <input
        id={label}
        name={label}
        type="number"
        value={valor}
        onChange={onChange}
      />
    </>
  );
}

function Simples() {

      const [mensalSimples, setMensalSimples] = useState(0);
    const [anualSimples, setAnualSimples] = useState(0);

    const onChange = (evt) => {
      const value = parseFloat(evt.target.value) || 0;

      if (evt.target.name === ' Juros Mensal Simples ') {
        setMensalSimples(value);
      } else if (evt.target.name === ' Juros Anual Simples ') {
        setMensalSimples(convertToMensalSimples(value));
      }
    }

    return (
      <div className="simples">
        <ConverterInput
          label=" Juros Anual Simples "
          valor={convertToAnualSimples(mensalSimples)}
          onChange={onChange}
        />
        <ConverterInput
          label=" Juros Mensal Simples "
          valor={mensalSimples}
          onChange={onChange}
        />
      </div>
    );
  }


  render(<Simples />, document.getElementById('sim'));  

//Convertor Composto

function convertToMensalComposto(valoranualComposto) {
  const total = ((1 + (valoranualComposto/100))**(1/12) -1)*100;
  return total; //.toFixed(2);
}

function convertToAnualComposto(valormensalComposto) {
  const total2 = ((1 + (valormensalComposto/100))**12 -1)*100;
  return total2; //.toFixed(2);
}


function Composto() {
  const [mensalComposto, setMensalComposto] = useState(0);
  const [anualComposto, setAnualComposto] = useState(0);

  const onChange = (evt) => {
    const value = parseFloat(evt.target.value) || 0;

    if (evt.target.name === ' Juros Mensal Composto ') {
      setMensalComposto(value);
    } else if (evt.target.name === ' Juros Anual Composto ') {
      setMensalComposto(convertToMensalComposto(value));
    }
  }

  return (
    <div className="simples">
      <ConverterInput
        label=" Juros Anual Composto "
        valor={convertToAnualComposto(mensalComposto)}
        onChange={onChange}
      />
      <ConverterInput
        label=" Juros Mensal Composto "
        valor={mensalComposto}
        onChange={onChange}
      />
    </div>
  );
}

render(<Composto />, document.getElementById('comp'));
