import React, { useState } from 'react';
import { render } from 'react-dom';
import './style.css';
import { Simples, Composto, Main } from './convJuros.js';
import { CompoundInterest } from './jurosComp.js';
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

function Form() {
  function handleSubmit(e) {
    e.preventDefault();
    console.log('Você clicou em enviar.');
  }}

export default function Home() {

  var menu = useState(0);
  function resultado() {
    if (menu === 1) {
      return(<Main />)
    };

    if (menu === 2) {
      return(<CompoundInterest />)
    }
  }

return (
  <div>
    <header>
      <div class="container" />
      <div id="logo">
        <img src="logo.png" alt="texto" height="400" width="400" />
      </div>
    </header>
    <div class="containerCentro1">
      <div class="containerCentro2">
        <div id="menu">

          <button class="button" onClick={alert}>
            Interest Converter
          </button>
          <button class="button" onClick={menu = 2}>Compound Interest Converter</button>
        </div>
      </div>
    </div>
    <div id="principal">

      <div class="efeito">

        <div class="container">

          {resultado}

        </div>
      </div>
    </div>
    <footer>Developed by Lucas Marinho</footer>
  </div>
)
}



render(<Home />, document.getElementById('root'));
