import React, { useState } from 'react';
import { render } from 'react-dom';
import './style.css';
import { Simples, Composto, Main } from './convJuros.js';
import { CompoundInterest } from './jurosComp.js';
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";



export default function Home() {

  function handleSubmit(e) {
    e.preventDefault();
    menu=1;
  }

  var menu = useState(0);
  function resultado() {
    if (menu === 1) {
      alert(<Main />)
    };

    if (menu === 2) {
      return(<CompoundInterest />)
    }
  }

return (
  <div>
    <header>
      <div class="container" />
      <div id="logo">
        <img src="logo.png" alt="texto" height="400" width="400" />
      </div>
    </header>
    <div class="containerCentro1">
      <div class="containerCentro2">
        <div id="menu">

          <form onSubmit={handleSubmit}>
          <button class="button" type="submit">
            Interest Converter
          </button>

          </form>
          <button class="button" onClick={menu = 2}>Compound Interest Converter</button>
        </div>
      </div>
    </div>
    <div id="principal">

      <div class="efeito">

        <div class="container">

          {resultado}

        </div>
      </div>
    </div>
    <footer>Developed by Lucas Marinho</footer>
  </div>
)
}



render(<Home />, document.getElementById('root'));
