import React, { useState } from 'react';
import { render } from 'react-dom';
import './App.css';

function convertToCelsius(tempFahrenheit) {
  return (tempFahrenheit - 32) * 5/9;
}

function convertToFahrenheit(tempCelsius) {
  return (tempCelsius * 9/5) + 32;
}

function ConverterInput({ label, temp, onChange }) {
  return (
    <>
      <label labelfor={label}>{label}</label>
      <input
        id={label}
        name={label}
        type="number"
        value={temp}
        onChange={onChange}
      />
    </>
  );
}

function App() {
  const [celsius, setCelsius] = useState(32);

  const onChange = (evt) => {
    const value = parseFloat(evt.target.value) || 0;

    if (evt.target.name === 'Juros Anual Simples') {
      setCelsius(value);
    } else if (evt.target.name === 'Juros Mensal Simples') {
      setCelsius(convertToCelsius(value));
    }
  }

  return (
    <div className="App">
      <ConverterInput
        label="Fahrenheit"
        temp={convertToFahrenheit(celsius)}
        onChange={onChange}
      />
      <ConverterInput
        label="Celsius"
        temp={celsius}
        onChange={onChange}
      />
    </div>
  );
}

export default App;
render(<App />, document.getElementById('root'));
