import React, { useState } from "react";
import { render } from "react-dom";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const CompoundInterest = () => {

  const [anualSimples, setAnualSimples] = useState()
  const [mesSimples, setMesSimples] = useState()

  return (
  <>
    <form>
    <TextField label="Juros Anual Simples" 
    variant="outlined"
    onChange={(e) => setAnualSimples(e.target.value)}
    />
    <TextField label="Juros ao Mês Simples"
    variant="filled"
    onChange={(e) => setMesSimples(e.target.value)}
     />
    </form>
  </>
  );
};

const CompoundInterest2 = () => {
  const [anualComposto, setAnualComposto] = useState()
  const [mesComposto, setmesComposto] = useState()
  return (
  <>
    <form>
    <TextField label="Juros Anual Composto"
    variant="outlined"
    onChange={(e) => setAnualComposto(e.target.value)}
    />
    <TextField label="Juros ao Mês Composto" 
    variant="filled"
    onChange={(e) => setmesComposto(e.target.value)}
    />
    </form>
  </>
  );
};

function converterMesSimples(anualSimples){
  return(anualSimples%12);
}

function converterAnoSimples(mesSimples){
  return(mesSimples*12);
}

