import React, { useState } from 'react';
import { render } from 'react-dom';
import './App.css';

function convertToAnoSimples(mesSimples) {
  return (mesSimples*12);
}

function convertToMesSimples(anoSimples) {
  return (anoSimples%12);
}


function ConverterInput({ label, valor, onChange }) {
  return (
    <>
      <label labelfor={label}>{label}</label>
      <input
        id={label}
        name={label}
        type="number"
        value={valor}
        onChange={onChange}
      />
    </>
  );
}

function App() {
  const [anoSimples, setAnoSimples] = useState();

  const onChange = (evt) => {
    const value = parseFloat(evt.target.value) || 0;

    if (evt.target.name === 'anoSimples') {
      setAnoSimples(value);
    } else if (evt.target.name === 'MesSimples') {
      setAnoSimples(convertToAnoSimples(value));
    }
  }

  return (
    <div className="App">
      <ConverterInput
        label="MesSimples"
        temp={convertToMesSimples(anoSimples)}
        onChange={onChange}
      />
      <ConverterInput
        label="AnoSimples"
        temp={anoSimples}
        onChange={onChange}
      />
    </div>
  );
}

export default App;
render(<App />, document.getElementById('root'));
