import React, { useState } from "react";
import { render } from "react-dom";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const CompoundInterest = () => {

  const [anualSimples, setAnualSimples] = useState(1)
  const [mesSimples, setMesSimples] = useState(1)

  return (
  <>
    <form>
    <TextField label="Juros Anual Simples" 
    variant="outlined"
    value={anualSimples}
    onChange={(e) => setAnualSimples(e.target.value)}
    />
    <TextField label="Juros ao Mês Simples"
    variant="filled"
    value={mesSimples}
    onChange={(e) => setMesSimples(e.target.value)}

     />
     <Button> Calcula </Button>
    </form>
  </>
  );
};

const CompoundInterest2 = () => {
  const [anualComposto, setAnualComposto] = useState()
  const [mesComposto, setmesComposto] = useState()
  return (
  <>
    <form>
    <TextField label="Juros Anual Composto"
    variant="outlined"
    onChange={(e) => setAnualComposto(e.target.value)} 
    />
    <TextField label="Juros ao Mês Composto" 
    variant="filled"
    onChange={(e) => setmesComposto(e.target.value)}
    />
    <Button> Calcula </Button>
    </form>
  </>
  );
};

<Button
variant="contained"
color="primary"
onClick={() => {
  converterAnoSimples();
  converterMesSimples();
}}
></Button>

function converterMesSimples(anualSimples, mesSimples){
  let num1 = anualSimples % 12;
  mesSimples = (num1) = useState();
}

function converterAnoSimples(mesSimples){
  return(mesSimples*12);
}

